package com.example.dictionary;public class Word {    private String Word;    private String Type;    private String Pronounce;    private String Meaning;    public Word( String word, String type, String pronounce, String meaning) {        Word = word;        Type = type;        Pronounce = pronounce;        Meaning = meaning;    }    public String getWord() {        return Word;    }    public void setWord(String word) {        Word = word;    }    public String getType() {        return Type;    }    public void setType(String type) {        Type = type;    }    public String getPronounce() {        return Pronounce;    }    public void setPronounce(String pronounce) {        Pronounce = pronounce;    }    public String getMeaning() {        return Meaning;    }    public void setMeaning(String meaning) {        Meaning = meaning;    }}